import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Home({ setLoggedIn }) {
  const [message, setMessage] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate(); // Hook for navigation

  // Check if the user is logged in when the component mounts
  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const response = await axios.get('http://localhost:5000/', { withCredentials: true });
        setIsLoggedIn(true);
        setMessage(`Welcome back, ${response.data.user}!`);
      } catch (err) {
        setIsLoggedIn(false);
        setMessage('Please log in or register to continue.');
      }
    };

    checkAuthStatus();
  }, []);

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:5000/logout', {}, { withCredentials: true });
      setLoggedIn(false);
      setIsLoggedIn(false);
      setMessage('You have logged out successfully.');
    } catch (err) {
      setMessage('Error while logging out. Please try again.');
    }
  };

  const goToSummarizer = () => {
    navigate('/summarizer'); // Redirect to Summarizer page
  };

  return (
    <div className="container mt-5">
      <h3>Home</h3>
      <div className="alert alert-info">{message}</div>

      {isLoggedIn ? (
        <div>
          <button className="btn btn-danger" onClick={handleLogout}>
            Logout
          </button>
          <button className="btn btn-success ml-2" onClick={goToSummarizer}>
            Go to Summarizer Tool
          </button>
        </div>
      ) : (
        <div>
          <button className="btn btn-primary" onClick={() => navigate('/login')}>
            Login
          </button>
          <button className="btn btn-secondary ml-2" onClick={() => navigate('/register')}>
            Register
          </button>
        </div>
      )}
    </div>
  );
}

export default Home;
