import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Home({ setLoggedIn }) {
  const [message, setMessage] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  // Check if the user is logged in when the component mounts
  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        await axios.get('http://localhost:5000/', { withCredentials: true });

        setIsLoggedIn(true);
        setMessage('Welcome to the Summarizer Tool');
      } catch (err) {
        setIsLoggedIn(false);
        setMessage('Please log in or register to continue.');
      }
    };

    checkAuthStatus();
  }, []);

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:5000/logout', {}, { withCredentials: true });
      setLoggedIn(false);
      setIsLoggedIn(false);
      setMessage('You have logged out successfully.');
      navigate('/login');
    } catch (err) {
      setMessage('Error while logging out. Please try again.');
    }
  };

  const goToSummarizer = () => {
    navigate('/summarizer');
  };

  // Inline styling
  const styles = {
    container: {
      maxWidth: '600px',
      margin: '0 auto',
      padding: '30px',
      textAlign: 'center',
      fontFamily: 'Arial, sans-serif'
    },
    header: {
      fontSize: '2.5rem',
      fontWeight: '600',
      color: '#333'
    },
    alert: {
      marginTop: '20px',
      backgroundColor: '#e7f5ff',
      padding: '15px',
      borderRadius: '8px',
      color: '#333',
      fontWeight: '500'
    },
    buttonGroup: {
      marginTop: '30px'
    },
    button: {
      fontSize: '1rem',
      padding: '10px 20px',
      borderRadius: '5px',
      cursor: 'pointer',
      margin: '10px',
      border: 'none'
    },
    loginBtn: {
      backgroundColor: '#007bff',
      color: 'white'
    },
    registerBtn: {
      backgroundColor: '#28a745',
      color: 'white'
    },
    logoutBtn: {
      backgroundColor: '#dc3545',
      color: 'white'
    },
    summarizerBtn: {
      backgroundColor: '#17a2b8',
      color: 'white'
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>Welcome to the Summarizer Tool</h1>
      <div style={styles.alert}>
        <p>{message}</p>
      </div>

      <div style={styles.buttonGroup}>
        {isLoggedIn ? (
          <>
            <button style={{ ...styles.button, ...styles.logoutBtn }} onClick={handleLogout}>
              Logout
            </button>
            <button style={{ ...styles.button, ...styles.summarizerBtn }} onClick={goToSummarizer}>
              Go to Summarizer Tool
            </button>
          </>
        ) : (
          <>
            <button style={{ ...styles.button, ...styles.loginBtn }} onClick={() => navigate('/login')}>
              Login
            </button>
            <button style={{ ...styles.button, ...styles.registerBtn }} onClick={() => navigate('/register')}>
              Register
            </button>
          </>
        )}
      </div>
    </div>
  );
}

export default Home;
